EndgateData.analyzeJsonNode = function(data)
    local ret = {};
    if data then
        function analyze(node,t)
            for i,k in pairs(t) do
                local a = k:get_value();
                if type(a) == "table" then
                    node[i] = {};
                    analyze(node[i],k);
                else
                    node[i] = k:get_value();
                end
            end
        end
        analyze(ret,data);
    end
    return ret;
end

